
var currentPost = 0;
var cardsSrc = [["https://goo.gl/ojLeJH", "https://goo.gl/1pVLFL", "https://goo.gl/Gwp4XM"], 
                ["https://goo.gl/4ytfNF", "https://goo.gl/jEkAU9", "https://goo.gl/CRtT9Z"],
                ["https://goo.gl/wXNRDs", "https://goo.gl/WYncVn", "data:image/png;base64,"],
                ["https://goo.gl/qUxVyJ", "data:image/jpeg;base64,", "data:image/jpeg;base64,"],
                ["https://goo.gl/trAKh4", "https://goo.gl/fppyxT", "https://goo.gl/cfhFg5"],
                ["https://goo.gl/1c35YQ", "https://goo.gl/JQrLVD", "https://goo.gl/B2MAbq"]];
var color = ["#92a8d1", "#80ced6", "#d5f4e6", "#f4e1d2", "#b5e7a0", "#eca1a6"];
setDivColor();
setBackgroundColor();

function clickPost(event) {

    let parent = event.target;
    while(parent.tagName.toLowerCase() != "div") {
        parent = parent.parentNode;
    }
    removeAllClass();

    parent.classList.add("clicked");
    parent.getElementsByTagName("p")[0].classList.add("clicked");
    displayImg(parent, true);
}

function displayImg(element, tuggle) {
    console.log("element: ", element.getElementsByTagName("img"));
    if (tuggle) {
        element.getElementsByTagName("img")[0].classList.add("display");
    } else {
        element.getElementsByTagName("img")[0].classList.remove("display");
    }
    
}

$(".post").click(clickPost);

function goNext() {
    removeAllClass();
    if (currentPost <= 2) {
        document.getElementById("post-" + (currentPost + 3)).style.display = "flex";
    }
    if (currentPost < 5) {
        document.getElementById("post-" + currentPost).style.display = "none";
        currentPost++;
        setCardSrc();
        $(".card").addClass("cardin");
        setBackgroundColor();
    }
    console.log("next current: ", currentPost);
}

function goBack() {
    removeAllClass();
    if (currentPost <= 3 && currentPost > 0) {
        document.getElementById("post-" + (currentPost + 2)).style.display = "none";
    }
    if (currentPost > 0) {
        document.getElementById("post-" + (currentPost - 1)).style.display = "flex";
        currentPost--;
        
        setCardSrc();
        $(".card").addClass("cardin");
        setBackgroundColor();
    }
    console.log("back current: ", currentPost);
}

$("#back").click(goBack);
$("#next").click(goNext);
// $("#background").click(removeAllClass);

function removeAllClass() {
    $(".clicked").removeClass("clicked");
    $(".display").removeClass("display");
    $(".card").removeClass("cardin");
}

function setCardSrc() {
    let cards = $("img.card");
    for (let i=0 ; i<3 ; i++) {
        cards[i].src = cardsSrc[currentPost][i];
    }
}

function setDivColor() {
    let div = document.getElementsByClassName("post");
    for (let i=0 ; i<div.length ; i++) {
        div[i].style.backgroundColor = color[i];
        // div[i].style.filter = "blur(3px)";
    }
}
function setBackgroundColor() {
    let div = document.getElementsByClassName("post");
    let background = document.getElementById("background");
    background.style.backgroundColor = div[currentPost].style.backgroundColor;
    document.body.style.backgroundColor = div[currentPost].style.backgroundColor;
}